import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";
import Nav from "./Nav.jsx";
import About from "./About";
import Tips from "./Tips.jsx";
import Auth from "./Login.jsx";
import TodoApp from "./TodoApp.jsx";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");

  // Removed authentication check to always show login first

  useEffect(() => {
    const storedTodos = localStorage.getItem('todos');
    if (storedTodos) {
      setTodos(JSON.parse(storedTodos));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const addTodo = (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    setTodos([...todos, { id: Date.now(), text, completed: false }]);
    setText("");
  };

  const toggleTodo = (id) => {
    setTodos(
      todos.map((t) =>
        t.id === id ? { ...t, completed: !t.completed } : t
      )
    );
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
  };

  function saveToFile(todos) {
    const content = todos
      .map(
        (t) =>
          `${t.completed ? "âœ”ï¸ Completed =>" : "âŒ Not Completed =>"} ${t.text}`
      )
      .join("\n");
    const blob = new Blob([content], { type: "text/plain" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = "todos.txt";
    a.click();
    URL.revokeObjectURL(url);
  }

  const handleLogin = async (credentials) => {
    // Simple mock authentication - in real app, this would call an API
    const mockUser = { email: credentials.email, name: credentials.email.split('@')[0] };
    localStorage.setItem('user', JSON.stringify(mockUser));
    setUser(mockUser);
    setIsAuthenticated(true);
  };

  const handleRegister = async (userData) => {
    // Simple mock registration - in real app, this would call an API
    const mockUser = { email: userData.email, name: userData.name };
    localStorage.setItem('user', JSON.stringify(mockUser));
    setUser(mockUser);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="app-container">
        <Nav 
          isAuthenticated={isAuthenticated} 
          user={user} 
          onLogout={handleLogout} 
        />

        <div className="content">
          <Routes>
            <Route
              path="/"
              element={<TodoApp todos={todos} setTodos={setTodos} text={text} setText={setText} addTodo={addTodo} toggleTodo={toggleTodo} deleteTodo={deleteTodo} saveToFile={saveToFile} />}
            />
            <Route 
              path="/todos" 
              element={<TodoApp />} 
            />
            <Route path="/About" element={<About />} />
            <Route path="/Tips" element={<Tips />} />
            <Route path="/login" element={<Auth onLogin={handleLogin} onRegister={handleRegister} isAuthenticated={isAuthenticated} user={user} />} />
          </Routes>
        </div>

        <footer>
          <p>ðŸŒŠ Stay productive & enjoy the waves of life ðŸŒŠ</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
