import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";
import Nav from "./Nav.jsx";
import About from "./About";
import Tips from "./Tips.jsx";
import Auth from "./Login.jsx";
import TodoApp from "./TodoApp.jsx";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");

  // Removed authentication check to always show login first

  useEffect(() => {
    const storedTodos = localStorage.getItem('todos');
    if (storedTodos) {
      setTodos(JSON.parse(storedTodos));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const addTodo = (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    setTodos([...todos, { id: Date.now(), text, completed: false }]);
    setText("");
  };

  const toggleTodo = (id) => {
    setTodos(
      todos.map((t) =>
        t.id === id ? { ...t, completed: !t.completed } : t
      )
    );
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
  };

  function saveToFile(todos) {
    const completed = todos.filter(t => t.completed);
    const pending = todos.filter(t => !t.completed);

    const html = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>My Todo List</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            margin: 0;
            padding: 20px;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            min-height: 100vh;
        }
        .container {
            max-width: 800px;
            margin: 0 auto;
            background: white;
            border-radius: 15px;
            box-shadow: 0 10px 30px rgba(0,0,0,0.2);
            overflow: hidden;
        }
        .header {
            background: linear-gradient(135deg, #2c3e50 0%, #34495e 100%);
            color: white;
            padding: 20px;
            text-align: center;
        }
        .header h1 {
            margin: 0;
            font-size: 2.5rem;
        }
        .section {
            margin: 20px;
        }
        .section h2 {
            color: #2c3e50;
            border-bottom: 3px solid #3498db;
            padding-bottom: 10px;
            margin-bottom: 15px;
        }
        table {
            width: 100%;
            border-collapse: collapse;
            margin-bottom: 20px;
        }
        th, td {
            padding: 12px 15px;
            text-align: left;
            border-bottom: 1px solid #ddd;
        }
        th {
            background-color: #f8f9fa;
            font-weight: 600;
            color: #2c3e50;
        }
        tr:nth-child(even) {
            background-color: #f8f9fa;
        }
        tr:hover {
            background-color: #e9ecef;
        }
        .status {
            font-weight: bold;
            padding: 4px 8px;
            border-radius: 4px;
            color: white;
        }
        .completed {
            background-color: #27ae60;
        }
        .pending {
            background-color: #e74c3c;
        }
        .summary {
            background: #ecf0f1;
            padding: 15px;
            margin: 20px;
            border-radius: 8px;
            text-align: center;
        }
        .summary p {
            margin: 5px 0;
            font-size: 1.1rem;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <h1>üìã My Todo List</h1>
            <p>Generated on ${new Date().toLocaleDateString()}</p>
        </div>

        <div class="summary">
            <p><strong>Total Tasks:</strong> ${todos.length}</p>
            <p><strong>Completed:</strong> ${completed.length} | <strong>Pending:</strong> ${pending.length}</p>
        </div>

        ${completed.length > 0 ? `
        <div class="section">
            <h2>‚úÖ Completed Tasks</h2>
            <table>
                <thead>
                    <tr>
                        <th>Status</th>
                        <th>Task</th>
                    </tr>
                </thead>
                <tbody>
                    ${completed.map(t => `
                        <tr>
                            <td><span class="status completed">Completed</span></td>
                            <td>${t.text}</td>
                        </tr>
                    `).join('')}
                </tbody>
            </table>
        </div>
        ` : ''}

        ${pending.length > 0 ? `
        <div class="section">
            <h2>‚ùå Pending Tasks</h2>
            <table>
                <thead>
                    <tr>
                        <th>Status</th>
                        <th>Task</th>
                    </tr>
                </thead>
                <tbody>
                    ${pending.map(t => `
                        <tr>
                            <td><span class="status pending">Pending</span></td>
                            <td>${t.text}</td>
                        </tr>
                    `).join('')}
                </tbody>
            </table>
        </div>
        ` : ''}

        ${todos.length === 0 ? `
        <div class="section">
            <h2>No Tasks Yet</h2>
            <p>Start adding tasks to see them here!</p>
        </div>
        ` : ''}
    </div>
</body>
</html>`;

    const blob = new Blob([html], { type: "text/html" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = "my-todo-list.html";
    a.click();
    URL.revokeObjectURL(url);
  }

  const handleLogin = async (credentials) => {
    // Simple mock authentication - in real app, this would call an API
    const mockUser = { email: credentials.email, name: credentials.email.split('@')[0] };
    localStorage.setItem('user', JSON.stringify(mockUser));
    setUser(mockUser);
    setIsAuthenticated(true);
  };

  const handleRegister = async (userData) => {
    // Simple mock registration - in real app, this would call an API
    const mockUser = { email: userData.email, name: userData.name };
    localStorage.setItem('user', JSON.stringify(mockUser));
    setUser(mockUser);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="app-container">
        <Nav 
          isAuthenticated={isAuthenticated} 
          user={user} 
          onLogout={handleLogout} 
        />

        <div className="content">
          <Routes>
            <Route
              path="/"
              element={<TodoApp todos={todos} setTodos={setTodos} text={text} setText={setText} addTodo={addTodo} toggleTodo={toggleTodo} deleteTodo={deleteTodo} saveToFile={saveToFile} />}
            />
            <Route 
              path="/todos" 
              element={<TodoApp />} 
            />
            <Route path="/About" element={<About />} />
            <Route path="/Tips" element={<Tips />} />
            <Route path="/login" element={<Auth onLogin={handleLogin} onRegister={handleRegister} isAuthenticated={isAuthenticated} user={user} onLogout={handleLogout} />} />
          </Routes>
        </div>

        <footer>
          <p>üåä Stay productive & enjoy the waves of life üåä</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
