import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";
import Nav from "./Nav.jsx";
import About from "./About";
import Tips from "./Tips.jsx";
import Auth from "./Login.jsx";
import TodoApp from "./TodoApp.jsx";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    if (token && storedUser) {
      setUser(JSON.parse(storedUser));
      setIsAuthenticated(true);
    }
  }, []);

  useEffect(() => {
    const storedTodos = localStorage.getItem('todos');
    if (storedTodos) {
      setTodos(JSON.parse(storedTodos));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const addTodo = (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    setTodos([...todos, { id: Date.now(), text, completed: false }]);
    setText("");
  };

  const toggleTodo = (id) => {
    setTodos(
      todos.map((t) =>
        t.id === id ? { ...t, completed: !t.completed } : t
      )
    );
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
  };

  function saveToFile(todos) {
    const completedTasks = todos.filter(t => t.completed);
    const pendingTasks = todos.filter(t => !t.completed);

    const htmlContent = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>My Todo Tasks</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            margin: 0;
            padding: 20px;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            min-height: 100vh;
        }
        .container {
            max-width: 800px;
            margin: 0 auto;
            background: white;
            border-radius: 15px;
            box-shadow: 0 10px 30px rgba(0,0,0,0.2);
            overflow: hidden;
        }
        h1 {
            text-align: center;
            color: #333;
            padding: 20px;
            margin: 0;
            background: #f8f9fa;
        }
        table {
            width: 100%;
            border-collapse: collapse;
        }
        th, td {
            padding: 15px;
            text-align: left;
            border-bottom: 1px solid #ddd;
        }
        th {
            background: #667eea;
            color: white;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }
        tr:nth-child(even) {
            background: #f8f9fa;
        }
        tr:hover {
            background: #e9ecef;
            transition: background 0.3s ease;
        }
        .status {
            padding: 5px 10px;
            border-radius: 20px;
            font-size: 0.8em;
            font-weight: bold;
            text-align: center;
            display: inline-block;
        }
        .completed {
            background: #d4edda;
            color: #155724;
        }
        .pending {
            background: #f8d7da;
            color: #721c24;
        }
        .section-title {
            font-size: 1.2em;
            font-weight: bold;
            margin: 20px 0 10px 0;
            color: #333;
            border-bottom: 2px solid #667eea;
            padding-bottom: 5px;
        }
        .summary {
            padding: 20px;
            background: #f8f9fa;
            text-align: center;
        }
        .summary p {
            margin: 5px 0;
            font-size: 1.1em;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>My Todo Tasks</h1>

        <div class="summary">
            <p>Total Tasks: ${todos.length}</p>
            <p>Completed: ${completedTasks.length} | Pending: ${pendingTasks.length}</p>
        </div>

        ${completedTasks.length > 0 ? `
        <div class="section-title">‚úÖ Completed Tasks</div>
        <table>
            <thead>
                <tr>
                    <th>Status</th>
                    <th>Task</th>
                </tr>
            </thead>
            <tbody>
                ${completedTasks.map(t => `
                <tr>
                    <td><span class="status completed">Completed</span></td>
                    <td>${t.text}</td>
                </tr>
                `).join('')}
            </tbody>
        </table>
        ` : ''}

        ${pendingTasks.length > 0 ? `
        <div class="section-title">‚ùå Pending Tasks</div>
        <table>
            <thead>
                <tr>
                    <th>Status</th>
                    <th>Task</th>
                </tr>
            </thead>
            <tbody>
                ${pendingTasks.map(t => `
                <tr>
                    <td><span class="status pending">Pending</span></td>
                    <td>${t.text}</td>
                </tr>
                `).join('')}
            </tbody>
        </table>
        ` : ''}
    </div>
</body>
</html>`;

    const blob = new Blob([htmlContent], { type: 'text/html' });
    const url = URL.createObjectURL(blob);

    const a = document.createElement('a');
    a.href = url;
    a.download = 'my-todos.html';
    a.click();
    URL.revokeObjectURL(url);
  }

  const handleLogin = async (credentials) => {
    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsAuthenticated(true);
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      throw new Error('Login failed');
    }
  };

  const handleRegister = async (userData) => {
    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsAuthenticated(true);
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      throw new Error('Registration failed');
    }
  };

  const handleLogout = () => {    localStorage.removeItem('token');    localStorage.removeItem('token');
    setUser(null);
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div className="app-container">
        <Nav 
          isAuthenticated={isAuthenticated} 
          user={user} 
          onLogout={handleLogout} 
        />

        <div className="content">
          <Routes>
            <Route
              path="/"
              element={<TodoApp todos={todos} setTodos={setTodos} text={text} setText={setText} addTodo={addTodo} toggleTodo={toggleTodo} deleteTodo={deleteTodo} saveToFile={saveToFile} />}
            />
            <Route 
              path="/todos" 
              element={<TodoApp />} 
            />
            <Route path="/About" element={<About />} />
            <Route path="/Tips" element={<Tips />} />
            <Route path="/login" element={<Auth onLogin={handleLogin} onRegister={handleRegister} isAuthenticated={isAuthenticated} user={user} onLogout={handleLogout} />} />
          </Routes>
        </div>

        <footer>
          <p>üåä Stay productive & enjoy the waves of life üåä</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
